def longest_common_subsequence(s1, s2):
    # Initialize a matrix of size s1.length x s2.length
    matrix = [[0 for _ in range(s2.length)] for _ in range(s1.length)]

    # Initialize the first row and column of the matrix with 1
    for i in range(s1.length):
        matrix[i][0] = 1

    for j in range(s2.length):
        matrix[0][j] = 1

    # Iterate through the matrix and update the values based on the current row and column
    for i in range(1, s1.length):
        for j in range(1, s2.length):
            if s1[i] == s2[j]:
                matrix[i][j] = matrix[i-1][j-1] + 1
            else:
                matrix[i][j] = max(matrix[i-1][j], matrix[i][j-1])

    # Return the length of the longest common subsequence
    return matrix[s1.length-1][s2.length-1]

